/**
 * ART STUDIO CORE ENGINE
 * Fields: p_name, p_desc, p_photo, p_discount, p_qty
 */

const artDatabase = [
    { 
        id: 1, 
        p_name: "Mirror Mosaic V1", 
        p_desc: "Hand-cut geometric mirrors on a premium wood base.", 
        p_photo: "data:image/jpeg;base64,",
        p_discount: "New Arrival",
        p_qty: 3,
        category: "Mirror Art"
    },
    { 
        id: 2, 
        p_name: "Azure Glass Inlay", 
        p_desc: "Fine mosaic using venetian glass tiles.", 
        p_photo: "https://images.unsplash.com/photo-1561214115-f2f134cc4912?w=800",
        p_discount: "10% Off",
        p_qty: 1,
        category: "Mosaic"
    },
    { 
        id: 3, 
        p_name: "Royal Elephant Folk", 
        p_desc: "Madhubani style pigment painting.", 
        p_photo: "https://images.unsplash.com/photo-1582555172866-f73bb12a2ab3?w=800",
        p_discount: null,
        p_qty: 12,
        category: "Folk"
    }
];

class ArtStudioApp {
    constructor() {
        this.init();
    }

    init() {
        this.renderGallery(artDatabase);
        this.setupEventListeners();
    }

    setupEventListeners() {
        // Mobile Toggle Logic
        const sidebar = document.getElementById('sidebarMenu');
        document.getElementById('mobileToggle').onclick = () => sidebar.classList.add('active');
        document.getElementById('closeSidebar').onclick = () => sidebar.classList.remove('active');

        // Search Logic
        document.getElementById('artSearch').oninput = (e) => {
            const query = e.target.value.toLowerCase();
            const filtered = artDatabase.filter(art => 
                art.p_name.toLowerCase().includes(query)
            );
            this.renderGallery(filtered);
        };
    }

    renderGallery(data) {
        const grid = document.getElementById('mainArtGrid');
        if (data.length === 0) {
            grid.innerHTML = `<div class="col-12 text-center py-5"><h4>No masterpieces found.</h4></div>`;
            return;
        }

        grid.innerHTML = data.map(item => `
            <div class="col-sm-6 col-lg-4">
                <article class="art-card shadow-sm bg-white">
                    <div class="img-frame">
                        <img src="${item.p_photo}" alt="${item.p_name}" loading="lazy">
                        ${item.p_discount ? `<span class="discount-tag">${item.p_discount}</span>` : ''}
                    </div>
                    <div class="p-4 text-center">
                        <p class="qty-tag mb-1">Available: ${item.p_qty} Pieces</p>
                        <h5 class="fw-bold mb-2">${item.p_name}</h5>
                        <p class="small text-muted mb-3">${item.p_desc}</p>
                        <button class="btn btn-dark btn-sm px-4 rounded-pill">Inquire Details</button>
                    </div>
                </article>
            </div>
        `).join('');
    }
}

// Start the Application
const app = new ArtStudioApp();